SELECT t.id, t.EVENT_NAME, t.EVENT_DATE, t.CREATE_DATE, t.EVENT_LEVEL, t.CREATOR, t.EVENT_CODE, t.EVENT_ID, t.MASTER_ID, t.GRADE_ONE, t.GRADE_TWO_A, t.GRADE_TWO_B, t.node, t.HOSP_NAME, t.PATIENT_NAME, t.HOSP_ID, decode(r.node, 2, '医院退回', 3, '省区退回', 4, '集团退回', '未知') as node_name, case when t.modify_date > r.modify_date then '历史退回' else '当前被退回' end as reback_name, c.EVENT_URL, c.EVENT_NAME as event_type_name, r.MODIFER, r.MODIFY_DATE as audit_date, r.OPINION, t.MODIFY_DATE FROM T_AE_BASIC_INFO t left join T_AE_OPERATION_RECORD r on t.id = r.BASIC_ID left join t_ae_event_config c on c.event_code = t.event_code where r.TYPE = 6
and exists (select 'X' from t_ae_basic_info pre where (pre.id = t.prev_id or pre.id = t.id) and pre.creator = #{cond.creator})
and t.node = #{cond.node}
and r.node = #{cond.rebackNode}
and t.event_code = #{cond.eventType}
and t.event_code in #{item}
and t.event_date between to_date(concat(#{cond.eventDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.eventDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and t.event_date = to_date(#{cond.eventDate}, 'YYYY-MM-DD')
and t.create_date between to_date(concat(#{cond.reportDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.reportDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and t.create_date between to_date(concat(#{cond.reportDate}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.reportDate}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and t.hosp_id = #{cond.hospId}
and t.hosp_id in
#{item}
and r.MODIFY_DATE >= t.MODIFY_DATE
and t.MODIFY_DATE > r.MODIFY_DATE
and t.node = -1
and t.grade_one_code in #{item}
and t.create_date between to_date(concat(#{cond.reportDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.reportDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and t.event_date between to_date(concat(#{cond.eventDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.eventDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and exists (select 'x' from t_ae_event_tags et where et.event_id = t.id and et.using_sign = 1 and et.tag_code in
#{item}
)
order by r.MODIFY_DATE desc

select * from ( select t.*, nvl((select max(report_times) from t_ae_basic_info t1 where t1.report_times > 1 and t1.prev_id = t.id ), 0) as max_report_times ,(select min(modify_date) from t_ae_operation_record toper where toper.basic_id = t.id) as min_oper_date, COALESCE(t1.unplan, t2.unplan, t3.unplan, t4.unplan) AS unplan, (select SUM(tt.compensation_amount) from t_ae_basic_info tt where (tt.prev_id = t.id or tt.id = t.id) or tt.master_id = t.id) as total_compensation_amount, (select SUM(tt.deduction_amount) from t_ae_basic_info tt where (tt.prev_id = t.id or tt.id = t.id) or tt.master_id = t.id) as total_deduction_amount, (select max(tt.amount_date) from t_ae_basic_info tt where (tt.prev_id = t.id or tt.id = t.id) or tt.master_id = t.id) as last_amount_date from T_AE_BASIC_INFO t LEFT JOIN t_ae_inp t1 ON t1.basic_id = t.id LEFT JOIN T_AE_OUTP_PATIENT t2 ON t2.basic_id = t.id LEFT JOIN T_AE_INFECTION t3 ON t3.basic_id = t.id LEFT JOIN T_AE_UNPL_REOPERATION t4 ON t4.basic_id = t.id
) tmp where 1=1
and tmp.max_report_times >= #{cond.maxReportTimes}
and tmp.total_compensation_amount >= #{cond.compensationAmount}
and tmp.total_compensation_amount <= #{cond.compensationUpAmount}
and tmp.total_deduction_amount >= #{cond.deductionAmount}
and tmp.total_deduction_amount <= #{cond.deductionUpAmount}
order by tmp.CREATE_DATE desc

