mapper.select:
select count(1) from T_AE_BASIC_INFO t left join t_ae_event_config c on c.event_code = t.event_code left join t_ae_focus_event fe on fe.basic_id = t.id and fe.modifer =
#{cond.operator}
0

where t.status = 0 and t.node > 1 and t.report_times = 1 and (t.id = t.master_id OR t.master_id IS NULL) and t.hosp_id < 9900 and t.hosp_id in( select hosp_id from T_AE_BASIC_INFO t where t.status = 0 and t.node > 1 and t.report_times = 1 and (t.id = t.master_id OR t.master_id IS NULL) and t.hosp_id < 9900
GROUP BY hosp_id HAVING COUNT(1) >= 2 )
and t.create_date between to_date(concat(#{cond.reportDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.reportDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and t.event_date between to_date(concat(#{cond.eventDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.eventDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and exists (select 'x' from t_ae_event_tags et where et.event_id = t.id and et.using_sign = 1 and et.tag_code in
#{item}
)
and t.grade_one_code in #{item}
and t.ehr_level = #{cond.ehrLevel}
order by create_date,hosp_id

WHERE t.status = 0 -- 事件状态为正常 AND t.node > 1 -- 事件节点大于 1，表示已开始处理 AND t.report_times = 1 -- 仅统计首次上报的事件 AND (t.id = t.master_id OR t.master_id IS NULL) -- 主事件或没有主事件 AND t.hosp_id < 9900 -- 针对特定医院的筛选 AND ( (t.hosp_id, t.GRADE_TWO_A_CODE) IN ( select hosp_id,GRADE_TWO_A_CODE from ( SELECT hosp_id, GRADE_TWO_A_CODE, COUNT(1) AS event_count FROM amcs.T_AE_BASIC_INFO t WHERE t.status = 0 -- 事件状态为正常 AND t.node > 1 -- 事件节点大于 1，表示已开始处理 AND t.report_times = 1 -- 仅统计首次上报的事件 AND (t.id = t.master_id OR t.master_id IS NULL) -- 主事件或没有主事件 AND t.hosp_id < 9900 -- 针对特定医院的筛选
GROUP BY hosp_id, GRADE_TWO_A_CODE HAVING COUNT(1) >= 3) ) )
and t.create_date between to_date(concat(#{cond.reportDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.reportDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and t.event_date between to_date(concat(#{cond.eventDateBegin}, ' 00:00:00'), 'YYYY-MM-DD hh24:mi:ss') and to_date(concat(#{cond.eventDateEnd}, ' 23:59:59'), 'YYYY-MM-DD hh24:mi:ss')
and exists (select 'x' from t_ae_event_tags et where et.event_id = t.id and et.using_sign = 1 and et.tag_code in
#{item}
)
and t.grade_one_code in #{item}
and t.ehr_level = #{cond.ehrLevel}
order by create_date,hosp_id

